import React, { Component } from "react";
import "./styles.scss";
class Keyboard extends Component {
  constructor(props) {
    this.state = {
      quote: "Tao khong noi vi tao lam ban tay khong con ",
    };
  }
  inputEvent = () => {
    let count = 0;
    let quoteInput = document.getElementById("quote_input");
    let arrayQuote = document.querySelectorAll("span");
    quoteInput.addEventListener("input", (e) => {
      let arrayValue = quoteInput.value.split(" ");
      if (e.data === " ") {
        if (arrayQuote[count].innerText == arrayValue[0]) {
          arrayQuote[count].classList.add("correct");
          arrayQuote[count].classList.remove("incorrect");
        }
        quoteInput.value = "";
        count++;
      }
    });
  };
  componentDidMount() {
    console.log("render");
    this.setUp();
    this.inputEvent();
  }
  render() {
    return (
      <div className="keyboard">
        <div id="quote_render">
          {this.state.quote.map((item) => (
            <span>{item}</span>
          ))}
        </div>
        <input id="quote_input" type="text" />
        <div className="key">A</div>
      </div>
    );
  }
}

export default Keyboard;
