import { useMemo, useState } from "react";
import { words, wordsAmount } from "../constant";

export default function GetWord(state) {
  const [data, setData] = useState();
  const [loading, setLoading] = useState(true);
  useMemo(() => {
    (async () => {
      try {
        if (state) {
          const arrayWords = [];
          const wordsByLanguage = words.VN;
          const wordsLength = wordsByLanguage.length;
          for (var i = 0; i < wordsAmount; i++) {
            const random = Math.floor(Math.random() * wordsLength);
            const word = wordsByLanguage[random];
            arrayWords.push(word);
          }
          setData(arrayWords);
          setLoading(false);
        } else {
          const arrayWordsEN = [];
          const wordsByLanguageEN = words.EN;
          const wordsLengthEN = wordsByLanguageEN.length;
          for (var i = 0; i < wordsAmount; i++) {
            const random = Math.floor(Math.random() * wordsLengthEN);
            const wordEN = wordsByLanguageEN[random];
            arrayWordsEN.push(wordEN);
          }
          setData(wordsByLanguageEN);
          setLoading(false);
        }
      } catch (error) {
        setLoading(true);
      }
    })();
  }, []);
  return { loading, data };
}
