import React, { Component } from "react";
import "./styles.scss";
class Keyboard extends Component {
  constructor(props) {
    super(props);
    this.state = {
      quote: "Tao khong noi vi tao lam ban tay khong con ",
    };
  }
  onChange = (e) => {
    let count = 0;
    let arrayQuote = document.querySelectorAll("span");
    console.log(e.target.value);
    let arrayValue = e.target.value.toString().trim();
    console.log(e.keyCode, "a", arrayValue);
    if (e.keyCode == 32) {
      console.log("a", arrayValue, "b", arrayQuote[count].innerText);
      if (arrayValue == arrayQuote[count].innerText) {
        arrayQuote[count].classList.add("correct");
        e.target.value = "";
      }
      e.target.value = "";
      count++;
    }
  };
  componentDidMount() {
    console.log("render");
  }
  render() {
    let arrayChar = this.state.quote.split(" ");
    return (
      <div className="keyboard">
        <div id="quote_render">
          {arrayChar &&
            arrayChar.map((item, index) => (
              <span key={index}>{item + " "}</span>
            ))}
        </div>
        <input
          id="quote_input"
          type="text"
          onKeyDown={(e) => this.onChange(e)}
        />
        <div className="key">A</div>
      </div>
    );
  }
}

export default Keyboard;
