import { click } from "@testing-library/user-event/dist/click";
import React, { Component } from "react";

import { keyboard } from "../constant";

class KeyBoard extends Component {
  constructor(props) {
    super(props);
    this.state = {
      onShift: false,
      onKey: "",
    };
    this.onKeyDown = this.onKeyDown.bind(this);
    this.onKeyUp = this.onKeyUp.bind(this);
    this.onShift = this.onShift.bind(this);
  }
  onShift(e) {
    // console.log("on shift func");
    // window.addEventListener("keydown", (e) => {
    //   if (e.key === "Shift") {
    //     this.setState((prevState) => ({
    //       onShift: !prevState.onShift,
    //     }));
    //     console.log("in onshift", this.state.onShift);
    //   }
    // });
  }
  onKeyDown(e) {
    console.log(e.key);
    switch (e.key) {
      case "Escape":
        this.setState({
          onKey: "~",
        });
        break;
      case "CapsLock":
        this.setState({
          onKey: "Caps",
        });
        break;
      case "Meta":
        this.setState({
          onKey: "⊞",
        });
        break;
      case "ArrowUp":
        this.setState({
          onKey: "⇧",
        });
        break;
      case "ArrowLeft":
        this.setState({
          onKey: "⇦",
        });
        break;
      case "ArrowRight":
        this.setState({
          onKey: "⇨",
        });
        break;
      case "ArrowDown":
        this.setState({
          onKey: "⇩",
        });
        break;
      default:
        this.setState({
          onKey: e.key,
        });
    }
  }

  addKeysEvent() {
    const keys = document.querySelectorAll(".keyboard__key.key");
    const typing__input = document.querySelector("#typing__input");
    keys.forEach((item, index) => {
      item.onclick = function () {
        typing__input.value += keys[index].innerText;
      };
    });
  }
  addFncEvent() {
    const fncs = document.querySelectorAll(".keyboard__key.fnc");
    const typing__input = document.querySelector("#typing__input");
    console.log(fncs);
    fncs.forEach((item, index) => {
      item.onclick = function () {
        console.log(fncs[index].innerText);
        switch (fncs[index].innerText) {
          case "Backspace":
            window.onPressKey("Backspace");
        }
      };
    });
  }
  onKeyUp() {
    this.setState({
      onKey: "",
    });
  }
  componentDidMount() {
    window.addEventListener("keydown", this.onKeyDown);
    window.addEventListener("keyup", this.onKeyUp);
    this.addKeysEvent();
    this.addFncEvent();
  }
  componentDidUpdate() {}
  render() {
    console.log("rerender in render keyborad");
    return (
      <div className="keyboard__wrapper">
        <div
          id="keyboard"
          className="keyboard"
          data-skinstance="simpleKeyboard"
        >
          <div className="keyboard__rows">
            {keyboard &&
              keyboard.map((item, index) => (
                <div key={index} className="keyboard__row">
                  {item.map((keys, index) => (
                    <button
                      key={index}
                      className={
                        this.state.onKey === keys.txt ||
                        this.state.onKey === keys.shiftTxt
                          ? `keyboard__key active ` +
                            ` keyboard__key${keys.u}` +
                            ` ${keys.type}`
                          : "keyboard__key" +
                            ` keyboard__key${keys.u}` +
                            ` ${keys.type}`
                      }
                    >
                      {this.state.onShift || this.state.onKey === "Shift"
                        ? keys.shiftTxt
                        : keys.txt}
                    </button>
                  ))}
                </div>
              ))}
          </div>
        </div>
      </div>
    );
  }
}

export default KeyBoard;
