import React, { Component } from "react";

import "./styles.scss";
import { vocabulary } from "../contans";

class Typing extends Component {
  constructor(props) {
    super(props);
    this.state = {
      arrayWords: [],
      quoteEN: "scale OOP java constructor ",
    };
    console.log("app rerender");
  }
  setUp = () => {
    let arrayWords = [];
    let wordsAmount = 400;
    let wordsLength = vocabulary.length;
    let random, word;
    for (var i = 0; i <= wordsAmount; i++) {
      random = Math.floor(Math.random() * wordsLength);
      word = vocabulary[random];
      arrayWords.push(word);
    }
    this.setState({
      arrayWords: arrayWords,
    });
  };
  typing = () => {
    let count = 0;
    let typping__render = document.querySelector("#typing__render");
    let arrayChar = document.querySelectorAll("span.typing__char");
    let charWith = Math.floor(
      typping__render.clientWidth / 2 / arrayChar[0].clientWidth
    );
    console.log(charWith);
    let arrayCharLength = arrayChar.length;
    const typing_input = document.getElementById("typing_input");
    typing_input.addEventListener("keyup", function (e) {
      if (typing_input.value) {
        if (e.key == " ") {
          let arrayValue = typing_input.value.toString().trim();
          if (arrayValue == arrayChar[count].innerText) {
            arrayChar[count].classList.add("correct");
          } else {
            arrayChar[count].classList.add("incorrect");
          }
          arrayChar[count].classList.remove("text_active");
          count++;
          typing_input.value = "";
          arrayChar[count].classList.add("text_active");
        }
      }
      if (count === arrayCharLength) {
        typing_input.readOnly = true;
      }
    });
  };
  componentWillMount() {
    this.setUp();
  }
  componentDidMount() {
    this.typing();
  }
  componentDidUpdate() {}
  render() {
    let arrayWordsRender = this.state.arrayWords;
    return (
      <div className="typing">
        <div id="typing__render" className="typing__render">
          <div
            className="typing__render__char"
            style={{ "--margin-char": "5px", "--fontSzie-char": "17px" }}
          >
            {Array.isArray(arrayWordsRender) &&
              arrayWordsRender.length > 0 &&
              arrayWordsRender.map((item, index) => (
                <span className="typing__char" key={index}>
                  {item}
                </span>
              ))}
          </div>
        </div>
        <input id="typing_input" className="typing_input" type="text" />
      </div>
    );
  }
}

export default Typing;
