import React, { Component } from "react";

import "./styles.scss";
import { vocabulary } from "../contans";

class Typing extends Component {
  constructor(props) {
    super(props);
    this.state = {
      arrayWords: [],
      quoteEN: "scale OOP java constructor ",
      counter: 60,
      start: true,
    };
    console.log("app rerender");
  }
  start = () => {
    this.setState({
      start: false,
    });
    const timer = setInterval(() => {
      this.setState((prevState) => {
        return { counter: prevState.counter - 1 };
      });
    }, 1000);
    console.log(timer);
    this.setState({
      timer: timer,
    });
  };
  setUp = () => {
    let arrayWords = [];
    let wordsAmount = 10;
    let wordsLength = vocabulary.length;
    let random, word;
    for (var i = 0; i <= wordsAmount; i++) {
      random = Math.floor(Math.random() * wordsLength);
      word = vocabulary[random];
      arrayWords.push(word);
    }
    console.log("alo ", this.state.timer);
    clearInterval(this.state.timer);
    this.setState({
      arrayWords: arrayWords,
      counter: 60,
      start: true,
    });
  };
  typing = () => {
    let count = 0;
    let traslateValue = 0;
    let stringInput = "";
    let typing__render = document.getElementById("typing__render");
    let typing__render__char = document.getElementById("typing__render__char");
    let arrayChar = document.querySelectorAll("span.typing__char");
    let averageCharWidth = Math.floor(
      typing__render.clientWidth / 2 / arrayChar[0].clientWidth
    );
    let arrayCharLength = arrayChar.length;
    const typing_input = document.getElementById("typing__input");
    arrayChar[count].classList.add("text_active");
    typing_input.addEventListener("keydown", (e) => {
      if (this.state.start) {
        this.start();
      }
      if (typing_input.value) {
        if (e.key == " ") {
          let arrayValue = typing_input.value.toString().trim();
          stringInput += typing_input.value.toString();
          if (arrayValue == arrayChar[count].innerText) {
            arrayChar[count].classList.add("correct");
          } else {
            arrayChar[count].classList.add("incorrect");
          }
          typing_input.value = "";
          arrayChar[count].classList.remove("text_active");
          if (count >= averageCharWidth) {
            traslateValue += arrayChar[count].clientWidth;
            typing__render__char.style.transform = `translateX(-${traslateValue}px)`;
          }
          if (count > arrayCharLength - 1) {
            console.log("1", count, arrayCharLength);
            typing_input.readOnly = true;
          } else {
            count++;
            try {
              arrayChar[count].classList.add("text_active");
            } catch (err) {}
          }
        }
      }
      if (count === arrayCharLength) {
        typing_input.readOnly = true;
      }
    });
  };
  componentDidMount() {
    this.setUp();
  }
  componentDidUpdate() {
    this.typing();
  }
  render() {
    let arrayWordsRender = this.state.arrayWords;
    return (
      <div className="typing">
        <div id="typing__render" className="typing__render">
          <div
            id="typing__render__char"
            className="typing__render__char"
            style={{ "--margin-char": "5px", "--fontSize-char": "17px" }}
          >
            {Array.isArray(arrayWordsRender) &&
              arrayWordsRender.length > 0 &&
              arrayWordsRender.map((item, index) => (
                <span className="typing__char" key={index}>
                  {item}
                </span>
              ))}
          </div>
        </div>
        <div className="typing__action">
          <input
            id="typing__input"
            className="typing__action__input"
            type="text"
            style={{ "--fontSize-input": "17px" }}
          />
          <div className="typing__action__counter">{this.state.counter}</div>
          <button
            className="typing__action__btn__restart"
            onClick={() => this.setUp()}
          >
            <svg
              xmlns="http://www.w3.org/2000/svg"
              fill="currentColor"
              viewBox="0 0 16 16"
            >
              <path d="M11.534 7h3.932a.25.25 0 0 1 .192.41l-1.966 2.36a.25.25 0 0 1-.384 0l-1.966-2.36a.25.25 0 0 1 .192-.41zm-11 2h3.932a.25.25 0 0 0 .192-.41L2.692 6.23a.25.25 0 0 0-.384 0L.342 8.59A.25.25 0 0 0 .534 9z" />
              <path
                fillRule="evenodd"
                d="M8 3c-1.552 0-2.94.707-3.857 1.818a.5.5 0 1 1-.771-.636A6.002 6.002 0 0 1 13.917 7H12.9A5.002 5.002 0 0 0 8 3zM3.1 9a5.002 5.002 0 0 0 8.757 2.182.5.5 0 1 1 .771.636A6.002 6.002 0 0 1 2.083 9H3.1z"
              />
            </svg>
          </button>
        </div>
      </div>
    );
  }
}

export default Typing;
