import React, { Component } from "react";
import "./styles.scss";
class Keyboard extends Component {
  constructor(props) {
    super(props);
    this.state = {
      count: 0,
    };
  }
  setUp = () => {
    let quoteInput = document.getElementById("quote_input");
    let quoteRender = document.getElementById("quote_render");
    let quote =
      "Tao khong noi vi tao lam ban tay khong con sach vi tu nho da dinh cham";
    quote.split("").forEach((item) => {
      let span = document.createElement("span");
      span.innerText = item;
      quoteRender.appendChild(characterSpan);
    });
    quoteInput.addEventListener("input", (e) => {
      let arrayQuote = quoteRender.querySelectorAll("span");
      let arrayValue = quoteInput.value.split("");
      arrayQuote.forEach((item, index) => {
        const char = arrayValue[index];
        if (char == null) {
          item.classList.remove("correct");
          item.classList.remove("incorrect");
        }
        if (char == item.innerText) {
          item.classList.add("correct");
          item.classList.remove("incorrect");
        } else {
          item.classList.add("incorrect");
          item.classList.remove("correct");
        }
      });
    });
  };
  onChange = (e) => {
    let a = e.target.value;
    const text = "abc";
    console.log(a);
    console.log(text.charAt(0));
  };
  componentDidMount() {
    this.setUp();
  }
  render() {
    return (
      <div className="keyboard">
        <div id="quote_render"></div>
        <input id="quote_input" type="text" />
        <div className="key">A</div>
      </div>
    );
  }
}

export default Keyboard;
