import React, { Component } from "react";
import "./styles.scss";
class Keyboard extends Component {
  constructor(props) {
    super(props);
    this.state = {
      quoteVN: "vì vĩ ví vỉ không lông bông công sông thông đông thu ",
      quoteEN: "scale OOP java constructor ",
    };
  }
  setUp = () => {
    let count = 0;
    let arrayQuote = document.querySelectorAll("span");
    let arrayQuoteLength = arrayQuote.length;
    const quote_input = document.getElementById("quote_input");
    arrayQuote[count].classList.add("text_active");
    quote_input.addEventListener("keyup", function (e) {
      if (quote_input.value) {
        if (e.key == " ") {
          let arrayValue = quote_input.value.toString().trim();
          if (arrayValue == arrayQuote[count].innerText) {
            arrayQuote[count].classList.add("correct");
          } else {
            arrayQuote[count].classList.add("incorrect");
          }
          arrayQuote[count].classList.remove("text_active");
          count++;
          quote_input.value = "";
          arrayQuote[count].classList.add("text_active");
        }
      }
      if (count === arrayQuoteLength - 1) {
        quote_input.readOnly = true;
      }
    });
  };
  componentDidMount() {
    console.log("render");
    this.setUp();
  }
  render() {
    let arrayQuote = this.state.quoteVN.split(" ");
    return (
      <div className="keyboard">
        <div id="quote_render">
          {arrayQuote &&
            arrayQuote.map((item, index) => <span key={index}>{item}</span>)}
        </div>
        <input id="quote_input" type="text" />
      </div>
    );
  }
}

export default Keyboard;
