import React, { Component } from "react";
import "./styles.scss";
class Keyboard extends Component {
  constructor(props) {
    super(props);
    this.state = {
      quote: "Tao khong noi vi tao lam ban tay khong con ",
    };
  }
  onChange = (e) => {
    let count = 0;
    let arrayQuote = document.querySelectorAll("span");
    let arrayValue = e.target.value.split(" ");
    console.log(e.key, "a", arrayValue[0], "b", arrayQuote[count].innerText);
    if (e.key == " ") {
      if (arrayValue[0] == arrayQuote[count].innerText) {
        arrayQuote[count].classList.add("correct");
      }
      e.target.value = "";
    }
  };
  componentDidMount() {
    console.log("render");
  }
  render() {
    let arrayChar = this.state.quote.split(" ");
    return (
      <div className="keyboard">
        <div id="quote_render">
          {arrayChar &&
            arrayChar.map((item, index) => <span key={index}>{item}</span>)}
        </div>
        <input
          id="quote_input"
          type="text"
          onKeyPress={(e) => this.onChange(e)}
        />
        <div className="key">A</div>
      </div>
    );
  }
}

export default Keyboard;
