import React, { Component } from "react";
import "./styles.scss";
class Keyboard extends Component {
  constructor(props) {
    super(props);
    this.state = {
      count: 0,
    };
  }
  setUp = () => {
    let quoteInput = document.getElementById("quote_input");
    let quoteRender = document.getElementById("quote_render");
    let quote = "Tao khong noi vi tao lam ban tay khong con ";
    quote.split(" ").forEach((item) => {
      let span = document.createElement("span");
      span.innerText = item;
      quoteRender.appendChild(span);
    });
    quoteInput.addEventListener("input", (e) => {
      let arrayQuote = quoteRender.querySelectorAll("span");
      let arrayValue = quoteInput.value.split(" ");
      if (e.data == " ") {
        console.log(this.state.count);
        if (arrayValue[0] == arrayQuote[this.state.count].innerText);
        {
          arrayQuote[this.state.count].classList.add("correct");
        }

        this.setState((prevState) => {
          return { count: prevState.count + 1 };
        });
        quoteInput.value = "";
        console.log(this.state.count);
      }
    });
  };
  onChange = (e) => {
    let a = e.target.value;
    const text = "abc";
    console.log(a);
    console.log(text.charAt(0));
  };
  componentDidMount() {
    this.setUp();
  }
  render() {
    return (
      <div className="keyboard">
        <div id="quote_render"></div>
        <input id="quote_input" type="text" />
        <div className="key">A</div>
      </div>
    );
  }
}

export default Keyboard;
