import React, { Component } from "react";

import "./styles.scss";
import { vocabulary, wordsAmount } from "../contans";

class Typing extends Component {
  constructor(props) {
    super(props);
    this.state = {
      arrayWords: [],
      counter: 10,
      start: true,
      ready: false,
      result: {},
      initValue: {
        count: 0,
        keystrokes: 0,
        traslateValue: 0,
        stringInput: "",
      },
    };
    this.setUp = this.setUp.bind(this);
  }
  startTimer = () => {
    this.setState({
      start: false,
    });
    const timer = setInterval(() => {
      if (this.state.counter == 0) {
        clearInterval(timer);
        this.props.result(this.state.initValue);
      } else {
        this.setState((prevState) => {
          return { counter: prevState.counter - 1 };
        });
      }
    }, 1000);
    this.setState({
      timer: timer,
    });
  };
  endWhenMax = (stringInArrears) => {
    clearInterval(this.state.timer);
    let string = this.state.initValue.stringInput + stringInArrears;
    let result = { ...this.state.result, stringInput: string };
    this.props.result(this.state.initValue);
    console.log("end when max");
  };
  initWords = () => {
    console.log("init word");
    let arrayWords = [];
    let wordsLength = vocabulary.length;
    let random, word;
    for (var i = 0; i < wordsAmount; i++) {
      random = Math.floor(Math.random() * wordsLength);
      word = vocabulary[random];
      arrayWords.push(word);
    }
    clearInterval(this.state.timer);
    this.setState({
      arrayWords: arrayWords,
      counter: 10,
      start: true,
      ready: true,
      result: {},
      initValue: {
        count: 0,
        keystrokes: 0,
        traslateValue: 0,
        stringInput: "",
      },
    });
  };
  typing = () => {
    const typing__render = document.getElementById("typing__render");
    const typing__render__char = document.getElementById(
      "typing__render__char"
    );
    const arrayChar = document.querySelectorAll("span.typing__char");
    const typing_input = document.getElementById("typing__input");

    typing__render__char.style.transform = "translate(0px)";
    arrayChar.forEach((item) => {
      item.classList.remove("incorrect", "correct", "text_active");
    });
    typing_input.focus();
    // typing_input.value = "";
    typing_input.readOnly = false;

    let translateWhenElement = Math.floor(
      typing__render.clientWidth / 2 / arrayChar[0].clientWidth
    );
    let arrayCharLength = arrayChar.length;

    arrayChar[this.state.initValue.count].classList.add("text_active");
    typing_input.addEventListener("keypress", (e) => {
      if (this.state.start) {
        this.startTimer();
      }
      this.setState((prevState) => ({
        initValue: {
          ...prevState.initValue,
          keystrokes: prevState.initValue.keystrokes + 1,
        },
      }));

      if (typing_input.value) {
        if (e.key == " ") {
          let arrayValue = typing_input.value.toString().trim();
          let stringInput = typing_input.value;
          this.setState((prevState) => ({
            initValue: {
              ...prevState.initValue,
              stringInput: prevState.initValue.stringInput + stringInput,
            },
          }));
          this.setState();
          arrayChar[this.state.initValue.count].classList.remove("text_active");

          if (arrayValue == arrayChar[this.state.initValue.count].innerText) {
            arrayChar[this.state.initValue.count].classList.add("correct");
          } else {
            arrayChar[this.state.initValue.count].classList.add("incorrect");
          }

          if (this.state.initValue.count >= translateWhenElement) {
            let plus = arrayChar[this.state.initValue.count].clientWidth;
            this.setState((prevState) => ({
              initValue: {
                ...prevState.initValue,
                traslateValue: prevState.initValue.traslateValue + plus,
              },
            }));
            typing__render__char.style.transform = `translateX(-${this.state.initValue.traslateValue}px)`;
          }
          if (this.state.initValue.count == arrayCharLength - 1) {
            this.endWhenMax(typing_input.value);
            typing_input.readOnly = true;
            const rsValue = {
              count: 0,
              keystrokes: 0,
              traslateValue: 0,
              stringInput: "",
            };
            this.setState({
              initValue: rsValue,
            });
          } else {
            this.setState((prevState) => ({
              initValue: {
                ...prevState.initValue,
                count: prevState.initValue.count + 1,
              },
            }));
            try {
              arrayChar[this.state.initValue.count].classList.add(
                "text_active"
              );
            } catch (err) {}
          }
          typing_input.value = "";
        }
      }
    });
  };
  async setUp() {
    await this.initWords();
    if (this.state.ready) {
      this.typing(0, 0, 0, "");
    }
  }
  componentDidMount() {
    this.setUp();
  }

  componentDidUpdate() {}
  render() {
    let arrayWordsRender = this.state.arrayWords;
    return (
      <div className="typing">
        <div id="typing__render" className="typing__render">
          <div
            id="typing__render__char"
            className="typing__render__char"
            style={{ "--margin-char": "5px", "--fontSize-char": "17px" }}
          >
            {Array.isArray(arrayWordsRender) &&
              arrayWordsRender.length > 0 &&
              arrayWordsRender.map((item, index) => (
                <span className="typing__char" key={index}>
                  {item}
                </span>
              ))}
          </div>
        </div>
        <div className="typing__action">
          <input
            id="typing__input"
            className="typing__action__input"
            type="text"
            style={{ "--fontSize-input": "17px" }}
            autoComplete="off"
          />
          <div className="typing__action__counter">{this.state.counter}</div>
          <button
            className="typing__action__btn__restart"
            onClick={() => this.setUp()}
          >
            <svg
              xmlns="http://www.w3.org/2000/svg"
              fill="currentColor"
              viewBox="0 0 16 16"
            >
              <path d="M11.534 7h3.932a.25.25 0 0 1 .192.41l-1.966 2.36a.25.25 0 0 1-.384 0l-1.966-2.36a.25.25 0 0 1 .192-.41zm-11 2h3.932a.25.25 0 0 0 .192-.41L2.692 6.23a.25.25 0 0 0-.384 0L.342 8.59A.25.25 0 0 0 .534 9z" />
              <path
                fillRule="evenodd"
                d="M8 3c-1.552 0-2.94.707-3.857 1.818a.5.5 0 1 1-.771-.636A6.002 6.002 0 0 1 13.917 7H12.9A5.002 5.002 0 0 0 8 3zM3.1 9a5.002 5.002 0 0 0 8.757 2.182.5.5 0 1 1 .771.636A6.002 6.002 0 0 1 2.083 9H3.1z"
              />
            </svg>
          </button>
        </div>
      </div>
    );
  }
}

export default Typing;
