import React, { Component } from "react";

import "./styles.scss";
import { vocabulary } from "../contans";

class Typing extends Component {
  constructor(props) {
    super(props);
    this.state = {
      arrayVocabulary: [],
      quoteEN: "scale OOP java constructor ",
    };
    console.log("app rerender");
  }
  setUp = () => {
    let arrayVocabulary = [];
    let vocabularyAmount = 400;
    let vocabularyLength = vocabulary.length;
    let random, quote;
    for (var i = 0; i <= vocabularyAmount; i++) {
      random = Math.floor(Math.random() * vocabularyLength);
      quote = vocabulary[random];
      arrayVocabulary.push(quote);
    }
    this.setState({
      arrayVocabulary: arrayVocabulary,
    });
  };
  typing = () => {
    let count = 0;
    let arrayChar = document.querySelectorAll("span");
    let arrayCharLength = arrayChar.length;
    const typing_input = document.getElementById("typing_input");
    typing_input.addEventListener("keyup", function (e) {
      if (typing_input.value) {
        if (e.key == " ") {
          let arrayValue = typing_input.value.toString().trim();
          if (arrayValue == arrayChar[count].innerText) {
            arrayChar[count].classList.add("correct");
          } else {
            arrayChar[count].classList.add("incorrect");
          }
          arrayChar[count].classList.remove("text_active");
          count++;
          typing_input.value = "";
          arrayChar[count].classList.add("text_active");
        }
      }
      if (count === arrayCharLength) {
        typing_input.readOnly = true;
      }
    });
  };
  componentWillMount() {
    this.setUp();
  }
  componentDidMount() {
    this.typing();
  }
  componentDidUpdate() {}
  render() {
    let arrayRender = this.state.arrayVocabulary;
    return (
      <div className="typing">
        <div className="typing__render">
          <div className="typing__char__render">
            {Array.isArray(arrayRender) &&
              arrayRender.length > 0 &&
              arrayRender.map((item, index) => <span key={index}>{item}</span>)}
          </div>
        </div>
        <input id="typing_input" className="typing_input" type="text" />
      </div>
    );
  }
}

export default Typing;
