import React, { Component } from "react";
import "./App.css";

import Typing from "./component/Typing";
import DialogResult from "./component/DialogResult";
import KeyBoard from "./component/KeyBoard";
import { bgImage, colorKey } from "./constant";

export default class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      result: {},
      showDialog: false,
      color: {
        key: colorKey.sun,
        bg: bgImage.sun,
        colorWhite: "#f4f4f4",
        colorBlack: "#000000",
        rain: false,
      },
    };
    this.color = {
      sun: { bg: bgImage.sun, colorWhite: "#f4f4f4", colorBlack: "#000000" },
      moon: {
        bg: bgImage.moon,
        colorWhite: "#000000",
        colorBlack: "#f4f4f4",
      },
    };
  }
  hiddenDialog = () => {
    let btnRestart = document.querySelector(".typing__action__btn__restart");
    this.setState({
      result: {},
      showDialog: false,
    });
    btnRestart.click();
  };
  getResult = (childData) => {
    this.setState({
      result: childData,
      showDialog: true,
    });
  };
  setColor = () => {
    const rain = Boolean(Math.random() > 0.3);
    this.setState((prevState) => {
      if (prevState.color.key === colorKey.sun) {
        const color = { ...this.color.moon, rain: rain };
        this.setState({
          color: color,
        });
      }
      if (prevState.color.key === colorKey.moon) {
        const color = { ...this.color.sun, rain: rain };
        this.setState({
          color: color,
        });
      }
    });
  };
  render() {
    return (
      <div className="App">
        <div className="container">
          <div className="content">
            <div
              className="content__top"
              style={{ "--color-white": "#f4f4f4", "--color-black": "#666" }}
            >
              <h1 className="content__top__name">Typing trainer</h1>
              <div className="content__top__option">
                <button onClick={this.setColor}>A</button>
              </div>
            </div>
            <Typing result={this.getResult} color={this.state.color} />
          </div>
          <DialogResult
            result={this.state.result}
            showDialog={this.state.showDialog}
            hiddenDialog={this.hiddenDialog}
          />
          <KeyBoard />
        </div>
      </div>
    );
  }
}
