import React, { Component } from "react";
import "./App.css";

import Typing from "./component/Typing";
import DialogResult from "./component/DialogResult";
import KeyBoard from "./component/KeyBoard";

export default class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      result: {},
      showDialog: false,
    };
  }
  hiddenDialog = () => {
    let btnRestart = document.querySelector(".typing__action__btn__restart");
    this.setState({
      result: {},
      showDialog: false,
    });
    btnRestart.click();
  };
  getResult = (childData) => {
    this.setState({
      result: childData,
      showDialog: true,
    });
  };
  render() {
    return (
      <div className="App">
        <div className="container">
          <h1 className="App name_project">Typing trainer</h1>
          <Typing result={this.getResult} />
          <DialogResult
            result={this.state.result}
            showDialog={this.state.showDialog}
            hiddenDialog={this.hiddenDialog}
          />
          <KeyBoard />
        </div>
      </div>
    );
  }
}
