import React, { Component } from "react";

import { keyboard } from "../constant";

class KeyBoard extends Component {
  constructor(props) {
    super(props);
    this.state = {
      onShift: false,
      onKey: "",
    };
    this.onKeyDown = this.onKeyDown.bind(this);
    this.onKeyUp = this.onKeyUp.bind(this);
    this.onShift = this.onShift.bind(this);
  }
  onShift(e) {
    console.log("on shift func");
    window.addEventListener("keydown", (e) => {
      if (e.key === "Shift" || e.key === "ShiftLeft") {
        this.setState((prevState) => ({
          onShift: !prevState.onShift,
        }));
        console.log("in onshift", this.state.onShift);
      }
    });
  }
  onKeyDown(e) {
    console.log(e.key);
    switch (e.key) {
      case "Escape":
        this.setState({
          onKey: "~",
        });
        break;
      case "CapsLock":
        this.setState({
          onKey: "Caps",
        });
        break;
      default:
        this.setState({
          onKey: e.key,
        });
    }
  }
  onKeyUp() {
    this.setState({
      onKey: "",
    });
  }
  componentDidMount() {
    window.addEventListener("keydown", this.onKeyDown);
    window.addEventListener("keydown", this.onShift);
    window.addEventListener("keyup", this.onKeyUp);
  }
  componentDidUpdate() {}
  render() {
    return (
      <div className="keyboard__wrapper">
        <div className="keyboard" data-skinstance="simpleKeyboard">
          <div className="keyboard__rows">
            {keyboard &&
              keyboard.map((item) => (
                <div className="keyboard__row">
                  {item.map((keys) => (
                    <div
                      className={
                        this.state.onKey === keys.txt ||
                        this.state.onKey === keys.shiftTxt
                          ? `keyboard__key active ` + ` ${keys.u}`
                          : "keyboard__key" + ` ${keys.u}`
                      }
                    >
                      {this.state.onShift ? (
                        <span>{keys.shiftTxt}</span>
                      ) : (
                        <span>{keys.txt}</span>
                      )}
                    </div>
                  ))}
                </div>
              ))}
          </div>
        </div>
      </div>
    );
  }
}

export default KeyBoard;
